import {
    useState,
    useEffect
} from 'react';
import PropTypes from 'prop-types';

function Secao(props) {

    let key = 0;

    function obterItemSecao4(codigo){
        let secao = props.secoes.filter(secao => {
                            return ((parseInt(secao.nivel) === 2) && (parseInt(secao.cod_parente) === parseInt(codigo)));
                        });
        if(secao.length === 0){
            return null;
        }
        let items = [];
        secao.map(item => {
            items.push(geraItem(item));
        });
        items.push(<li key={key++}>Nova Secao 4 | Novo Documento</li>)
        return (items);
    }

    function obterSecao4(codigo){
        return (
            <ul key={key++}>
                <li key={key++}>Nova Secao 4 | Novo Documento</li>
                {obterItemSecao2(codigo)}
            </ul>
        );
    }

    function obterItemSecao3(codigo){
        let secao = props.secoes.filter(secao => {
                            return ((parseInt(secao.nivel) === 3) && (parseInt(secao.cod_parente) === parseInt(codigo)));
                        });
        if(secao.length === 0){
            return null;
        }
        let items = [];
        secao.map(item => {
            items.push(geraItem(item));
        });
        items.push(<li key={key++}>Nova Secao 3 | Novo Documento</li>)
        return (items);
    }

    function obterSecao3(codigo){
        return (
            <ul key={key++}>
                <li key={key++}>Nova Secao 3 | Novo Documento</li>
                {obterItemSecao3(codigo)}
            </ul>
        );
    }

    function obterItemSecao2(codigo){
        let secao = props.secoes.filter(secao => {
                            return ((parseInt(secao.nivel) === 2) && (parseInt(secao.cod_parente) === parseInt(codigo)));
                        });
        if(secao.length === 0){
            return null;
        }
        let items = [];
        secao.map(item => {
            items.push(geraItem(item));
            items.push(obterSecao3(item.codigo));
        });
        items.push(<li key={key++}>Nova Secao 2 | Novo Documento</li>)
        return (items);
    }

    function obterSecao2(codigo){
        return (
            <ul key={key++}>
                <li key={key++}>Nova Secao 1 | Novo Documento</li>
                {obterItemSecao2(codigo)}
            </ul>
        );
    }

    function obterItemSecao1(){
        let secao = props.secoes.filter(secao => {
                                return parseInt(secao.nivel) === 1;
                            });
        if(secao.length === 0){
            return null;
        }
        let items = [];
        secao.map(item => {
            items.push(geraItem(item));
            items.push(obterSecao2(item.codigo));
        });
        return (items);
    }

    function geraItem(item){
        return(
            <li key={key++}>{item.descricao}</li>
        );
    }

    return (
        <ul key={key++}>
            <li key={key++}>Nova Secao 1</li>    
            {obterItemSecao1()}
        </ul>
    );
}

Secao.propTypes = {
    secoes: PropTypes.array.isRequired
}

export default Secao;
