import PropTypes from 'prop-types';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
    faEdit, 
    faTrashAlt,
    faThumbsUp,
    faThumbsDown,
} from '@fortawesome/free-solid-svg-icons';
import { 
    Image
} from 'react-bootstrap';

function ItensTabelaNoticias(props) {

    const URL_IMG = 'http://localhost/restful/img/';

    return ( 
        props.noticias.map(noticia => 
            <tr 
                key={noticia.codigo}>
                <td className="align-middle"
                    style={{ textAlign: 'center' }}>
                    {noticia.codigo}
                </td>
                <td 
                    style={{ textAlign: 'center' }}>
                    <Image 
                        src={URL_IMG + noticia.capa}
                        thumbnail />
                </td>
                <td className="align-middle">
                    {noticia.titulo}
                </td>
                <td  className="align-middle"
                    style={{ textAlign: 'center' }}>
                    <Button 
                        className={'btn btn-light btn-sm'} >
                        <FontAwesomeIcon 
                            icon={faThumbsUp}
                            // onClick={props.handleExibirEditar(noticia.codigo)}
                             />
                    </Button>
                </td>
                <td  className="align-middle"
                    style={{ textAlign: 'center' }}>
                    <span 
                        className={'btn btn-warning btn-sm'} >
                        <FontAwesomeIcon icon={faEdit} />
                    </span>
                </td>
                <td className="align-middle"
                    style={{ textAlign: 'center' }}>
                    <span 
                        className={'btn btn-danger btn-sm'} >
                        <FontAwesomeIcon icon={faTrashAlt} />
                    </span>
                </td>
            </tr>
        )
    );
}

ItensTabelaNoticias.propTypes = {
    noticias: PropTypes.array.isRequired, 
    handleExibirEditar: PropTypes.func.isRequired
}

export default ItensTabelaNoticias;
