import PropTypes from 'prop-types';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
    faEdit, 
    faTrashAlt,
    faThumbsUp,
    faThumbsDown,
} from '@fortawesome/free-solid-svg-icons';
import { 
    Image,
    Button
} from 'react-bootstrap';
import RemoverTarefa from './remover-tarefa';

function ItensTabelaNoticias(props) {

    const URL_IMG = 'http://localhost/restful/img/';

    function handleEditarNoticia(event, noticia){
        event.preventDefault();
        props.handleExibirEditar(noticia.codigo)
    }

    return ( 
        props.noticias.map(noticia => 
            <tr 
                key={noticia.codigo}>
                <td className="align-middle"
                    style={{ textAlign: 'center' }}>
                    {noticia.codigo}
                </td>
                <td 
                    style={{ textAlign: 'center' }}>
                    <Image 
                        src={URL_IMG + noticia.capa}
                        thumbnail />
                </td>
                <td className="align-middle">
                    {noticia.titulo}
                </td>
                <td  className="align-middle"
                    style={{ textAlign: 'center' }}>
                    <Button 
                        className={'btn btn-light btn-sm'} >
                        <FontAwesomeIcon 
                            icon={faThumbsUp}/>
                    </Button>
                </td>
                <td  className="align-middle"
                    style={{ textAlign: 'center' }}>
                    <Button 
                        className={'btn btn-warning btn-sm'}
                        onClick={(event) => handleEditarNoticia(event, noticia)} >
                        <FontAwesomeIcon icon={faEdit} />
                    </Button>
                </td>
                <td className="align-middle"
                    style={{ textAlign: 'center' }}>
                    <Button 
                        className={'btn btn-danger btn-sm'} >
                        <FontAwesomeIcon icon={faTrashAlt} />
                    </Button>
                </td>
            </tr>
        )
    );
}

ItensTabelaNoticias.propTypes = {
    noticias: PropTypes.array.isRequired, 
    handleExibirEditar: PropTypes.func.isRequired
}

export default ItensTabelaNoticias;
