import {
    useState,
    useEffect
} from 'react';
import PropTypes from 'prop-types';

function Secao(props) {

    let key = 0;

    function geraItem(item){
        return(
            <li key={key++}>{item.descricao}</li>
        );
    }

    function obterItemSecao4(){
        let secao = props.secoes.filter(secao => {
                        return parseInt(secao.nivel) === 4;
                    });
        if(secao.length === 0){
            return null;
        }
        let items = [];
        secao.map(item => {
            items.push(geraItem(item));
        });
        items.push(<li>Novo Documento</li>)
        items.push(obterSecao4());
        return (items);
    }

    function obterSecao4(){
        return (
            <ul>
                {obterItemSecao4()}
                <li>Nova Secao 4</li>        
            </ul>
        );
    }

    function obterItemSecao3(){
        let secao = props.secoes.filter(secao => {
                            return parseInt(secao.nivel) === 3;
                        });
        if(secao.length === 0){
            return null;
        }
        let items = [];
        secao.map(item => {
            items.push(geraItem(item));
        });
        items.push(<li>Nova Secao | Novo Documento</li>)
        items.push(obterSecao4());
        return (items);
    }

    function obterSecao3(){
        return (
            <ul>
                {obterItemSecao3()}
                <li>Nova Secao 3</li>        
            </ul>
        );
    }

    function obterItemSecao2(){
        let secao = props.secoes.filter(secao => {
                            return parseInt(secao.nivel) === 2;
                        });
        if(secao.length === 0){
            return null;
        }
        let items = [];
        secao.map(item => {
            items.push(geraItem(item));
        });
        items.push(<li>Nova Secao | Novo Documento</li>)
        items.push(obterSecao3());
        return (items);
    }

    function obterSecao2(){
        return (
            <ul>
                {obterItemSecao2()}
                <li>Nova Secao 2</li>        
            </ul>
        );
    }

    function obterItemSecao1(){
        let secao = props.secoes.filter(secao => {
                                return parseInt(secao.nivel) === 1;
                            });
        if(secao.length === 0){
            return null;
        }
        let items = [];
        secao.map(item => {
            items.push(geraItem(item));
        });
        items.push(<li key={key++}>Nova Secao | Novo Documento</li>)
        items.push(obterSecao2());
        return (items);
    }

    return (
        <ul>
            {obterItemSecao1()}
            <li key={key++}>Nova Secao 1</li>        
        </ul>
    );
}

Secao.propTypes = {
    secoes: PropTypes.array.isRequired
}

export default Secao;
