import { 
    Row,
    Form,
    Col,
    Image
} from 'react-bootstrap';
import PropTypes from 'prop-types';

function GaleriaImagens(props) {

    const URL_IMG = 'http://localhost/restful/img/';

    return ( 
        <div>
            {props.imagens.map((img, index) => {
                var resto = index % 4;
                if(resto === 1) {
                    <Form.Group 
                        as={Row} 
                        className={visivelImagens()}>
                        <Col 
                            md={3}>
                            <Image 
                                src={URL_IMG + img.capa}
                                thumbnail />
                        </Col>
                    </Form.Group>   
                } else if(resto === 2) {
                    <Form.Group 
                        as={Row} 
                        className={visivelImagens()}>
                        <Col 
                            md={3}>
                            <Image 
                                src={URL_IMG + img.capa}
                                thumbnail />
                        </Col>
                        <Col 
                            md={3}>
                            <Image 
                                src={URL_IMG + img.capa}
                                thumbnail />
                        </Col>
                    </Form.Group>   
                }
            })}
        </div>
    );
}

GaleriaImagens.propTypes = {
    visivel: PropTypes.bool.isRequired,
    imagens: PropTypes.array.isRequired
}

export default GaleriaImagens;
