import { useState } from 'react';
import PropTypes from 'prop-types';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {  
    faSortDown
} from '@fortawesome/free-solid-svg-icons';
import { 
    Button,
    Modal
} from 'react-bootstrap';
import axios from 'axios';
import Documento from '../../../models/documento.model';

function DownDocumento(props) {

    const API_URL_UPDATE_DOCUMENTO = 'http://localhost/restful/documento/';

    const [exibirModalErro, setExibirModalErro] = useState(false);
    const [messagem, setMessagem] = useState('');

    function handleFecharModalErro(){
        setExibirModalErro(false);
        props.setCarregarBalancetes(true);
    }

    async function handleDownDocumento(event){
        event.preventDefault();
        try {
            const novoDocumento = new Documento('', props.documento.secao);
            const formData = new FormData();
            formData.append('data', JSON.stringify(novoDocumento));
            formData.append('method', 'down');
            let { data } = await axios.put(API_URL_UPDATE_DOCUMENTO + props.documento.codigo, formData);

            if(data.status==='success'){
                setMessagem(data.response);
                props.setCarregarBalancetes(true);
            } else {
                setMessagem("Erro Down do Registro: " + data.response);
                console.log(messagem);
            }
        } catch(err) {
            setMessagem("Erro Down do Registro: " + err.message);
            console.log(messagem);
        }
    }

    return ( 
        <div>
            <FontAwesomeIcon
                className={'btn btn-outline-dark btn-sm'}
                onClick={handleDownDocumento} 
                icon={faSortDown} />
            <Modal 
                show={exibirModalErro} 
                onHide={handleFecharModalErro}>
                <Modal.Header closeButton>
                    <Modal.Title>
                        Erro
                    </Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    {messagem}
                </Modal.Body>
                <Modal.Footer>
                    <Button 
                        variant="warning"
                        onClick={handleFecharModalErro}>
                        Fechar
                    </Button>
                </Modal.Footer>
            </Modal>
        </div>
    );
}

DownDocumento.propTypes = {
    documento: PropTypes.object.isRequired,
    setCarregarBalancetes: PropTypes.func.isRequired,
}

export default DownDocumento;
