import {
    useState,
    useEffect
} from 'react';
import PropTypes from 'prop-types';

function Secao(props) {

    let key = 0;
    let secao = 0;

    function obterItemSecao4(codigo){
        let secao = props.secoes.filter(secao => {
                            return ((parseInt(secao.nivel) === 2) && (parseInt(secao.cod_parente) === parseInt(codigo)));
                        });
        if(secao.length === 0){
            return null;
        }
        let items = [];
        secao.map(item => {
            items.push(geraItem(item));
        });
        return (items);
    }

    function obterItemSecao3(codigo){
        let secao = props.secoes.filter(secao => {
                            return ((parseInt(secao.nivel) === 3) && (parseInt(secao.cod_parente) === parseInt(codigo)));
                        });
        if(secao.length === 0){
            return null;
        }
        let items = [];
        secao.map(item => {
            items.push(geraItem(item));
            items.push(obterSecao(item.codigo, 4));
        });
        return (items);
    }

    function obterItemSecao2(codigo){
        let secao = props.secoes.filter(secao => {
                            return ((parseInt(secao.nivel) === 2) && (parseInt(secao.cod_parente) === parseInt(codigo)));
                        });
        if(secao.length === 0){
            return null;
        }
        let items = [];
        secao.map(item => {
            items.push(geraItem(item));
            items.push(obterSecao(item.codigo, 3));
        });
        return (items);
    }

    function obterItemSecao1(){
        let secao = props.secoes.filter(secao => {
                                return parseInt(secao.nivel) === 1;
                            });
        if(secao.length === 0){
            return null;
        }
        let items = [];
        secao.map(item => {
            items.push(geraItem(item));
            items.push(obterSecao(item.codigo, 2));
        });
        return (items);
    }

    function geraItem(item){
        return(
            <li key={key++}>{item.descricao}</li>
        );
    }

    function obterSecao(codigo, nivel){
        if(nivel === 1){
            return obterItemSecao1();
        }
        else if(nivel === 2){
            return obterItemSecao2();
        }
        else if(nivel === 3){
            return obterItemSecao3();
        }
        else {
            return obterItemSecao4();
        }
    }

    return (
        <ul key={key++}>
            <li key={key++}>Nova Secao 1</li>    
            {obterSecao(0, 1)}
        </ul>
    );
}

Secao.propTypes = {
    secoes: PropTypes.array.isRequired
}

export default Secao;
