import { 
    Row,
    Form,
    Col,
    Image
} from 'react-bootstrap';
import PropTypes from 'prop-types';

function GaleriaImagens(props) {

    const URL_IMG = 'http://localhost/restful/img/';

    function visivel() {
        return visivelImagens() ? 'mt-3' : 'hidden';
    }

    return ( 
        <Form.Group 
            as={Row} 
            className={visivelImagens()}>
                {props.imagens.map(img => {
                    <Col 
                        md={3}>
                        <Image 
                            src={URL_IMG + img.capa}
                            thumbnail />
                    </Col>
            })}
        </Form.Group>
    );
}

GaleriaImagens.propTypes = {
    visivel: PropTypes.bool.isRequired,
    imagens: PropTypes.array.isRequired
}

export default GaleriaImagens;
