import './dashboard.css';
import {
  useState,
  useEffect,
} from 'react';
import { 
  BrowserRouter, 
  Routes, 
  Route 
} from 'react-router-dom';
import Login from './components/login/login';
import Home from './components/home/home';
import Noticias from './components/noticias/noticias';
import Balancetes from './components/balancetes/balancetes';
import Usuarios from './components/usuarios/usuarios';

function Dashboard() {

  const [carregarToken, setCarregarToken] = useState(true);
  const [token, setToken] = useState(true);

  useEffect(() => {

    function obterToken(){
        setToken(sessionStorage.getItem('token'));
    }

    if(carregarToken){
        obterToken();
        setCarregarToken(false);
    }
  }, [carregarToken]);

  if(!token) {
    return <Login setCarregarToken={setCarregarToken} />
  }

  return (
    <BrowserRouter>
      {/* <Routes> */}
      <Switch>
        <Route path="/dashboard/" element={<Home />} exact />
        <Route path="/dashboard/noticias" element={<Noticias />} exact />
        <Route path="/dashboard/balancetes" element={<Balancetes />} exact />
        <Route path="/dashboard/usuarios" element={<Usuarios />} exact />
      </Switch>
      {/* </Routes> */}
    </BrowserRouter>
  );
}

export default Dashboard;
