import {
    useState,
    useEffect,
} from 'react';
import Cabecalho from '../../utils/cabecalho';
import Menu from '../../utils/menu';
import Secao from './secao';
import { 
    Container,
    Row,
    Col,
} from 'react-bootstrap';
import axios from 'axios';

function Balancetes() {

    const API_URL_LISTAR_SECAO = 'http://localhost/restful/secao';
    const API_URL_LISTAR_DOCUMENTO = 'http://localhost/restful/documento';

    const [secoes, setSecoes] = useState([]);
    const [documentos, setDocumentos] = useState([]);
    const [carregarBalancetes, setCarregarBalancetes] = useState(true);
    
    useEffect(() => {

        async function obterBalancetes(){
            try{
                console.log("teste");
                let { secao } = await axios.get(API_URL_LISTAR_SECAO);
                console.log(secao.data);
                setSecoes(secao.data);
                let { documento } = await axios.get(API_URL_LISTAR_DOCUMENTO);
                console.log(documento.data);
                setDocumentos(documento.data);
            } catch(err){
                setSecoes([]);
            }
        }

        if(carregarBalancetes){
            obterBalancetes();
            setCarregarBalancetes(false);
        }
    }, [carregarBalancetes]);

    return ( 
        <div>
            <Cabecalho />
            <Container 
                fluid>
                <Row>
                    <Col 
                        md={2}
                        className="vh-100" >
                        <Menu />
                    </Col>
                    <Col md={10} >
                        <h2 
                            className="mt-3">
                            Balancetes
                        </h2>
                        <Secao
                            secoes={secoes}
                            documentos={documentos} />
                    </Col>
                </Row>
            </Container>
        </div> 
    );
}

export default Balancetes;
