import {
    useState,
    useEffect
} from 'react';
import PropTypes from 'prop-types';

function Secao(props) {

    let key = 0;
    const [secao1, setSecao1] = useState([]);
    const [secao2, setSecao2] = useState([]);
    const [secao3, setSecao3] = useState([]);
    const [secao4, setSecao4] = useState([]);
    const [carregarSecoes, setCarregarSecoes] = useState(true);

    useEffect(() => {

        function obterSecoes(){
            let listaSecao = JSON.parse(props.secoes);
            let listaSecao1 = listaSecao.filter(secao => {
                    return parseInt(secao.nivel) === 1;
                });
            console.log(listaSecao1);
            // setSecao1(props.secao.filter(secao => {
            //     return parseInt(secao.nivel) === 1;
            // }));
            // setSecao2(props.secao.filter(secao => {
            //     return parseInt(secao.nivel) === 2;
            // }));
            // setSecao3(props.secao.filter(secao => {
            //     return parseInt(secao.nivel) === 3;
            // }));
            // setSecao4(props.secao.filter(secao => {
            //     return parseInt(secao.nivel) === 4;
            // }));
        }

        if(carregarSecoes){
            obterSecoes();
            setCarregarSecoes(false);
        }
    }, [carregarSecoes]);

    function geraItem(item){
        return(
            <li key={key++}>{item.descricao}</li>
        );
    }

    function obterItemSecao4(){
        if(props.secao4.length === 0){
            return null;
        }
        let items = [];
        props.secao4.map(item => {
            items.push(geraItem(item));
        });
        items.push(<li>Nova Secao | Novo Documento</li>)
        items.push(obterSecao4());
        return (items);
    }

    function obterSecao4(){
        return (
            <ul>
                {obterItemSecao4()}
                <li>Nova Secao</li>        
            </ul>
        );
    }

    function obterItemSecao3(){
        if(props.secao3.length === 0){
            return null;
        }
        let items = [];
        props.secao3.map(item => {
            items.push(geraItem(item));
        });
        items.push(<li>Nova Secao | Novo Documento</li>)
        items.push(obterSecao4());
        return (items);
    }

    function obterSecao3(){
        return (
            <ul>
                {obterItemSecao3()}
                <li>Nova Secao</li>        
            </ul>
        );
    }

    function obterItemSecao2(){
        if(props.secao2.length === 0){
            return null;
        }
        let items = [];
        props.secao2.map(item => {
            items.push(geraItem(item));
        });
        items.push(<li>Nova Secao | Novo Documento</li>)
        items.push(obterSecao3());
        return (items);
    }

    function obterSecao2(){
        return (
            <ul>
                {obterItemSecao2()}
                <li>Nova Secao</li>        
            </ul>
        );
    }

    function obterItemSecao1(){
        if(secao1.length === 0){
            return null;
        }
        let items = [];
        secao1.map(item => {
            items.push(geraItem(item));
        });
        items.push(<li key={key++}>Nova Secao | Novo Documento</li>)
        // items.push(obterSecao2());
        return (items);
    }

    return (
        props.secoes.map(secao => 
            <span></span>
        )
        // <ul>
        //     {obterItemSecao1()}
        //     <li key={key++}>Nova Secao</li>        
        // </ul>
    );
}

Secao.propTypes = {
    secoes: PropTypes.array.isRequired
}

export default Secao;
