import {
    useState,
    useEffect,
} from 'react';
import { 
    Row,
    Col,
    Table,
    Form,
    Button,
} from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus } from '@fortawesome/free-solid-svg-icons';
import { Link } from "react-router-dom";
import axios from 'axios';
import ItensTabelaNoticias from './itens-tabela-noticias';

function TabelaNoticias() {

    const API_URL_LISTAR_NOTICIAS = 'http://localhost/restful/noticias';

    const [noticias, setNoticias] = useState([]);
    const [carregarNoticias, setCarregarNoticias] = useState(true);

    useEffect(() => {

        async function obterNoticias(){

            try{
                let { data } = await axios.get(API_URL_LISTAR_NOTICIAS);
                setNoticias(data.data);
            } catch(err){
                setNoticias([]);
            }
        }

        if(carregarNoticias){
            obterNoticias();
            setCarregarNoticias(false);
        }
    }, [carregarNoticias]);

    return ( 
        <div className="hidden">
            <h2 className="mt-3">Notícias</h2>
            <Table 
                striped 
                bordered 
                hover 
                className="mt-3 hidden">
                <thead>
                    <tr>
                        <th colSpan={3}>
                        <Row>
                            <Col md={2} style={{ textAlign: 'center' }}>
                                <span style={{ 
                                        display: 'inline-block',
                                        verticalAlign: 'middle',
                                        lineHeight: 'normal' }}>
                                    Pesquisa
                                </span>
                            </Col>
                            <Col md={9}>
                                <Form.Control
                                    className="filtro"
                                    type="text" >
                                </Form.Control>
                            </Col>
                        </Row>
                        </th>
                        <th colSpan={2} style={{ textAlign: 'center' }}>
                            <Button variant="primary">
                                <FontAwesomeIcon icon={faPlus} />
                                &nbsp;
                                Inserir
                            </Button>
                        </th>
                    </tr>
                    <tr>
                        <th width="10%" style={{ textAlign: 'center' }}>
                            Código
                        </th>
                        <th width="20%" style={{ textAlign: 'center' }}>
                            Capa
                        </th>
                        <th>
                            Descrição
                        </th>
                        <th width="10%">
                            &nbsp;
                        </th>
                        <th width="10%">
                            &nbsp;
                        </th>
                    </tr>
                </thead>
                <tbody>
                    <ItensTabelaNoticias 
                        noticias={noticias}
                        recarregarNoticias={setCarregarNoticias} />
                </tbody>
            </Table>
        </div>
     );
}

export default TabelaNoticias;
