import {
    useState,
    useEffect
} from 'react';
import PropTypes from 'prop-types';

function Secao(props) {

    let key = 0;
    let nivel = 1;

    function obterSecao(codigo){
        let secao = [];
        if(nivel == 1){
            secao = props.secoes.filter(secao => {
                return parseInt(secao.nivel) === nivel;
            });
        }
        else {
            secao = props.secoes.filter(secao => {
                return ((parseInt(secao.nivel) === nivel) && (parseInt(secao.cod_parente) === parseInt(codigo)));
            });
        }
        
        if(secao.length === 0){
            return null;
        }
        nivel++;
        let items = [];
        secao.map(item => {
            items.push(geraItem(item));
            items.push(obterSecao(item.codigo));
        });
        return(items);
    }

    function geraItem(item){
        return(
            <li key={key++}>{item.descricao}</li>
        );
    }

    return (
        <ul key={key++}>
            <li key={key++}>Nova Secao 1</li>    
            {obterSecao("0")}
        </ul>
    );
}

Secao.propTypes = {
    secoes: PropTypes.array.isRequired
}

export default Secao;
