import { useState } from 'react';
import PropTypes from 'prop-types';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {  
    faSortDown
} from '@fortawesome/free-solid-svg-icons';
import { 
    Button,
    Modal
} from 'react-bootstrap';
import axios from 'axios';
import Secao from '../../../models/secao.model';

function UpSecao(props) {

    const API_URL_UPDATE_SECAO = 'http://localhost/restful/secao/';

    const [exibirModalErro, setExibirModalErro] = useState(false);
    const [messagem, setMessagem] = useState('');

    function handleFecharModalErro(){
        setExibirModalErro(false);
        props.setCarregarBalancetes(true);
    }

    async function handleUpSecao(event){
        event.preventDefault();
        try {
            const novoSecao = new Secao(props.secao.codparente, '', '');
            const formData = new FormData();
            formData.append('data', JSON.stringify(novoSecao));
            formData.append('method', 'up');
            let { data } = await axios.put(API_URL_UPDATE_SECAO + props.secao.codigo, formData);
            if(data.status==='success'){
                setMessagem(data.response);
                props.setCarregarBalancetes(true);
            } else {
                setMessagem("Erro Down do Registro: " + data.response);
                setExibirModalErro(true);
            }
        } catch(err) {
            setMessagem("Erro Down do Registro: " + err.message);
            setExibirModalErro(true);
        }
    }

    return ( 
        <div>
            <Button 
                className={'btn btn-outline-dark btn-sm'}
                onClick={handleUpSecao} >
                <FontAwesomeIcon icon={faSortDown} />
            </Button>
            <Modal 
                show={exibirModalErro} 
                onHide={handleFecharModalErro}>
                <Modal.Header closeButton>
                    <Modal.Title>
                        Erro
                    </Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    {messagem}
                </Modal.Body>
                <Modal.Footer>
                    <Button 
                        variant="warning"
                        onClick={handleFecharModalErro}>
                        Fechar
                    </Button>
                </Modal.Footer>
            </Modal>
        </div>
    );
}

UpSecao.propTypes = {
    secao: PropTypes.object.isRequired,
    setCarregarBalancetes: PropTypes.func.isRequired,
}

export default UpSecao;
