import {
    useState,
    useEffect,
} from 'react';
import Cabecalho from '../../utils/cabecalho';
import Menu from '../../utils/menu';
import ItensBalancetes from './itens-balancetes';
import { 
    Container,
    Row,
    Col,
} from 'react-bootstrap';
import axios from 'axios';

function Balancetes() {

    const API_URL_LISTAR_SECAO = 'http://localhost/restful/secao';
    const API_URL_LISTAR_DOCUMENTO = 'http://localhost/restful/documento';

    const [secoes, setSecoes] = useState([]);
    const [documentos, setDocumentos] = useState([]);
    const [carregarBalancetes, setCarregarBalancetes] = useState(true);
    const [exibirItens, setExibirItens] = useState(true);
    const [exibirEditarSecao, setExibirEditarSecao] = useState(false);
    const [exibirEditarDocumento, setExibirEditarDocumento] = useState(false);
    
    useEffect(() => {

        async function obterSecao(){
            let { data } = await axios.get(API_URL_LISTAR_SECAO);
            console.log(data.data);
            setSecoes(data.data);
        }

        async function obterDocumento(){
            let { data } = await axios.get(API_URL_LISTAR_DOCUMENTO);
            console.log(data.data);
            setDocumentos(data.data);
        }

        if(carregarBalancetes){
            obterSecao();
            obterDocumento();
            setCarregarBalancetes(false);
        }
    }, [carregarBalancetes]);

    return ( 
        <div>
            <Cabecalho />
            <Container 
                fluid>
                <Row>
                    <Col 
                        md={2}
                        className="vh-100" >
                        <Menu />
                    </Col>
                    <Col md={10} >
                        <h2 
                            className="mt-3">
                            Balancetes
                        </h2>
                        <ItensBalancetes
                            visivel={exibirItens}
                            secoes={secoes}
                            documentos={documentos} />
                    </Col>
                </Row>
            </Container>
        </div> 
    );
}

export default Balancetes;
