import {
    useState,
} from 'react';
import { 
    Card,
    Button,
    Container,
    Row,
    Col,
    Form,
} from 'react-bootstrap';
import Noticia from '../../models/noticia.model';
import axios from 'axios';
import PropTypes from 'prop-types';

function EditarNoticia(props) {

    const URL_CADASTRAR_NOTICIA = 'http://localhost/restful/noticias';

    const [imagem, setImagem] = useState();
    const [titulo, setTitulo] = useState('');
    const [conteudo, setConteudo] = useState('');

    async function cadastrarNoticia(event){
        try {
            event.preventDefault();
            const formData = new FormData();
            const novaNoticia = new Noticia(conteudo, titulo);
            formData.append('image', imagem);
            formData.append('data', JSON.stringify(novaNoticia));
            console.log(novaNoticia)
            let { data } = await axios.post(URL_CADASTRAR_NOTICIA, formData);
            console.log(data.data)
        } catch (e) {

        }
    }

    function visivel() {
        return props.visivel ? 'mt-3' : 'hidden';
    }

    function handleImage(event){
        setImagem(event.target.files[0]);
      }

    function handleTxtTitulo(event){
        setTitulo(event.target.value);
    }

    function handleTxtConteudo(event){
        setConteudo(event.target.value);
    }

    return ( 
        <Card 
            className={visivel()}>
            <Card.Header>
                <h2>
                    Notícia
                </h2>
            </Card.Header>
            <Card.Body>
                <Container >
                    <Form 
                        onSubmit={cadastrarNoticia}>
                        <Form.Group
                            as={Row}>
                            <Form.Label
                                    column
                                    md={2}>
                                Codigo:
                            </Form.Label>
                            <Form.Label
                                    column
                                    md={10}>
                            </Form.Label>
                        </Form.Group>
                        <Form.Group
                            as={Row}
                            className="mt-2">
                            <Form.Label
                                    column
                                    md={2}>
                                Capa (PNG ou JPG):
                            </Form.Label>
                            <Col 
                                md={10}>
                                <Form.Control
                                    type="file"
                                    onChange={handleImage}
                                    accept="image/png, image/jpeg" />
                            </Col>
                        </Form.Group>
                        <Form.Group
                            as={Row}
                            className="mt-2">
                            <Form.Label
                                    column
                                    md={2}>
                                Título:
                            </Form.Label>
                            <Col 
                                md={10}>
                                <Form.Control
                                    type="text"
                                    value={titulo} 
                                    onChange={handleTxtTitulo} />
                            </Col>
                        </Form.Group>
                        <Form.Group
                            as={Row}
                            className="mt-2">
                            <Form.Label
                                    column
                                    md={2}>
                                Descrição:
                            </Form.Label>
                            <Col 
                                md={10}>
                                <Form.Control
                                     as="textarea" 
                                     rows={10}
                                     value={conteudo} 
                                     onChange={handleTxtConteudo} />
                            </Col>
                        </Form.Group>
                        <Form.Group 
                            as={Row} 
                            className="mt-3">
                            <Col 
                                className="text-end"
                                md={6}>
                                <Button 
                                    variant="danger"
                                    type="submit">
                                    Cancelar
                                </Button>
                            </Col>
                            <Col 
                                className="text-start"
                                md={6}>
                                <Button 
                                    variant="primary" 
                                    type="submit">
                                    Salvar
                                </Button>
                            </Col>
                        </Form.Group>
                    </Form>
                </Container>
            </Card.Body>
        </Card>
     );
}

EditarNoticia.propTypes = {
    visivel: PropTypes.bool.isRequired,
    handleExibirTabela: PropTypes.func.isRequired
}

export default EditarNoticia;
