import {
    useState,
    useEffect,
} from 'react';
import Cabecalho from '../../utils/cabecalho';
import Menu from '../../utils/menu';
import Secao from './secao';
import { 
    Container,
    Row,
    Col,
} from 'react-bootstrap';
import axios from 'axios';

function Balancetes() {

    const API_URL_LISTAR_SECAO = 'http://localhost/restful/secao';

    const [secao, setSecao] = useState([]);
    const [secao1, setSecao1] = useState([]);
    const [secao2, setSecao2] = useState([]);
    const [secao3, setSecao3] = useState([]);
    const [secao4, setSecao4] = useState([]);
    const [carregarBalancetes, setCarregarBalancetes] = useState(true);
    
    useEffect(() => {

        async function obterBalancetes(){
            try{
                let { data } = await axios.get(API_URL_LISTAR_SECAO);
                console.log(data.data);
                setSecao(data.data);
                console.log(secao);
                setSecao1(secao.filter(s => {
                    return s.nivel === 1;
                }));
                setSecao2(secao.filter(s => {
                    return s.nivel === 2;
                }));
                setSecao3(secao.filter(s => {
                    return s.nivel === 3;
                }));
                setSecao4(secao.filter(s => {
                    return s.nivel === 4;
                }));
                console.log(secao1);
            } catch(err){
                setSecao([]);
                setSecao1([]);
                setSecao2([]);
                setSecao3([]);
                setSecao4([]);
            }
        }

        if(carregarBalancetes){
            obterBalancetes();
            setCarregarBalancetes(false);
        }
    }, [carregarBalancetes]);

    return ( 
        <div>
            <Cabecalho />
            <Container 
                fluid>
                <Row>
                    <Col 
                        md={2}
                        className="vh-100" >
                        <Menu />
                    </Col>
                    <Col md={10} >
                        <h2 
                            className="mt-3">
                            Balancetes
                        </h2>
                        <Secao
                            secao1={secao1}
                            secao2={secao2}
                            secao3={secao3}
                            secao4={secao4}/>
                    </Col>
                </Row>
            </Container>
        </div> 
    );
}

export default Balancetes;
