import {
    useState,
    useEffect,
} from 'react';
import { 
    Card,
    Button,
    Container,
    Row,
    Col,
    Form,
    Modal
} from 'react-bootstrap';
import Noticia from '../../models/noticia.model';
import axios from 'axios';
import PropTypes from 'prop-types';

function EditarNoticia(props) {

    const URL_CADASTRAR_NOTICIA = 'http://localhost/restful/noticias';

    const [imagem, setImagem] = useState();
    const [titulo, setTitulo] = useState('');
    const [conteudo, setConteudo] = useState('');
    const [exibirModal, setExibirModal] = useState(false);
    const [exibirModalErro, setExibirModalErro] = useState(false);

    useEffect(() => {

        function obterNoticia(){
            const noticiaObj = props.noticias.filter(
                noticia => noticia.codigo === parseInt(props.codNoticia)
            )[0];
            // setTitulo(noticiaObj.titulo);
            // setConteudo(noticiaObj.conteudo);
            console.log(noticiaObj);
            setTitulo(props.codNoticia);
            
        }

        if(props.carregarNoticia){
            obterNoticia();
            props.setCarregarNoticia(false);
        }
    }, [props.carregarNoticia]);

    async function cadastrarNoticia(event){
        try {
            event.preventDefault();
            const formData = new FormData();
            const novaNoticia = new Noticia(conteudo, null, titulo);
            formData.append('image', imagem);
            formData.append('data', JSON.stringify(novaNoticia));
            await axios.post(URL_CADASTRAR_NOTICIA, formData);
            setExibirModal(true);
        } catch (err) {
            setExibirModalErro(true);
        }
    }

    function visivel() {
        return props.visivel ? 'mt-3' : 'hidden';
    }

    function handleImage(event){
        setImagem(event.target.files[0]);
      }

    function handleTxtTitulo(event){
        setTitulo(event.target.value);
    }

    function handleTxtConteudo(event){
        setConteudo(event.target.value);
    }

    function handleFecharModal(){
        setExibirModal(false);
        props.handleExibirTabela();
        props.recarregarNoticias(true);
    }

    function handleFecharModalErro(){
        setExibirModalErro(false);
        props.handleExibirTabela();
        props.recarregarNoticias(true);
    }

    return ( 
        <div
            className={visivel()}>
            <Card >
                <Card.Header>
                    <h2>
                        Notícia
                    </h2>
                </Card.Header>
                <Card.Body>
                    <Container >
                        <Form 
                            onSubmit={cadastrarNoticia}>
                            <Form.Group
                                as={Row}>
                                <Form.Label
                                        column
                                        md={2}>
                                    Codigo:
                                </Form.Label>
                                <Form.Label
                                        column
                                        md={10}>
                                    
                                </Form.Label>
                            </Form.Group>
                            <Form.Group
                                as={Row}
                                className="mt-2">
                                <Form.Label
                                        column
                                        md={2}>
                                    Capa (PNG ou JPG):
                                </Form.Label>
                                <Col 
                                    md={10}>
                                    <Form.Control
                                        type="file"
                                        onChange={handleImage}
                                        accept="image/png, image/jpeg" />
                                </Col>
                            </Form.Group>
                            <Form.Group
                                as={Row}
                                className="mt-2">
                                <Form.Label
                                        column
                                        md={2}>
                                    Título:
                                </Form.Label>
                                <Col 
                                    md={10}>
                                    <Form.Control
                                        type="text"
                                        value={titulo} 
                                        onChange={handleTxtTitulo} />
                                </Col>
                            </Form.Group>
                            <Form.Group
                                as={Row}
                                className="mt-2">
                                <Form.Label
                                        column
                                        md={2}>
                                    Descrição:
                                </Form.Label>
                                <Col 
                                    md={10}>
                                    <Form.Control
                                        as="textarea" 
                                        rows={10}
                                        value={conteudo} 
                                        onChange={handleTxtConteudo} />
                                </Col>
                            </Form.Group>
                            <Form.Group 
                                as={Row} 
                                className="mt-3">
                                <Col 
                                    className="text-end"
                                    md={6}>
                                    <Button 
                                        variant="danger"
                                        onClick={props.handleExibirTabela} >
                                        Cancelar
                                    </Button>
                                </Col>
                                <Col 
                                    className="text-start"
                                    md={6}>
                                    <Button 
                                        variant="primary" 
                                        type="submit">
                                        Salvar
                                    </Button>
                                </Col>
                            </Form.Group>
                        </Form>
                    </Container>
                </Card.Body>
            </Card>
            <Modal 
                    show={exibirModal} 
                    onHide={handleFecharModal} >
                    <Modal.Header closeButton>
                        <Modal.Title>
                            Sucesso
                        </Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                        Notícia salva com sucesso!
                    </Modal.Body>
                    <Modal.Footer>
                        <Button 
                            variant="success"
                            onClick={handleFecharModal}>
                            Continuar
                        </Button>
                    </Modal.Footer>
                </Modal>
                <Modal 
                    show={exibirModalErro} 
                    onHide={handleFecharModalErro}>
                    <Modal.Header closeButton>
                        <Modal.Title>
                            Erro
                        </Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                        Erro ao adicionar tarefa, tente novamente em instantes.
                    </Modal.Body>
                    <Modal.Footer>
                        <Button 
                            variant="warning"
                            onClick={handleFecharModalErro}>
                            Fechar
                        </Button>
                    </Modal.Footer>
                </Modal>
        </div>
        
     );
}

EditarNoticia.propTypes = {
    visivel: PropTypes.bool.isRequired,
    handleExibirTabela: PropTypes.func.isRequired,
    recarregarNoticias: PropTypes.func.isRequired,
    noticias: PropTypes.array.isRequired,
    codNoticia: PropTypes.number.isRequired,
    carregarNoticia: PropTypes.bool.isRequired,
    setCarregarNoticia: PropTypes.func.isRequired,
}

export default EditarNoticia;
