import {
    useState,
    useEffect,
    useRef,
} from 'react';
import { 
    Card,
    Button,
    Container,
    Row,
    Col,
    Form,
    Modal,
    Image
} from 'react-bootstrap';
import Noticia from '../../models/noticia.model';
import axios from 'axios';
import PropTypes from 'prop-types';
import GaleriaImagens from './galeria-imagens';

function EditarNoticia(props) {

    const URL_IMG = 'http://localhost/restful/img/';
    const URL_CADASTRAR_NOTICIA = 'http://localhost/restful/noticias';
    const API_URL_LISTAR_GALERIA = 'http://localhost/restful/galeria/';

    const [capa, setCapa] = useState('');
    const [galeria, setGaleria] = useState([]);
    const [imagem, setImagem] = useState();
    const [codigo, setCodigo] = useState('');
    const [titulo, setTitulo] = useState('');
    const [conteudo, setConteudo] = useState('');
    const [exibirModal, setExibirModal] = useState(false);
    const [exibirModalErro, setExibirModalErro] = useState(false);
    const [exibirImagens, setExibirImagens] = useState(false);

    const capaElement = useRef();
    const galeriaElement = useRef();

    useEffect(() => {

        async function obterNoticia(){
            if(props.codNoticia > 0){
                const noticiaObj = props.noticias.filter(
                    noticia => noticia.codigo === props.codNoticia
                )[0];

                let { data } = await axios.get(API_URL_LISTAR_GALERIA + noticiaObj.codigo);
                setGaleria(data.data);
                setCodigo(noticiaObj.codigo);
                setTitulo(noticiaObj.titulo);
                setConteudo(noticiaObj.conteudo);
                setCapa(noticiaObj.capa);
                setExibirImagens(true);
            } else {
                setGaleria([]);
                setCodigo('');
                setTitulo('');
                setConteudo('');
                setExibirImagens(false);
            }
            capaElement.current.value = "";
            galeriaElement.current.value = "";
        }

        if(props.carregarNoticia){
            obterNoticia();
            props.setCarregarNoticia(false);
        }
    }, [props.carregarNoticia]);

    async function salvarNoticia(event){
        try {
            event.preventDefault();
            if(props.codNoticia === 0){
                const novaNoticia = new Noticia(conteudo, null, titulo);
                const formData = new FormData();
                formData.append('image', imagem);
                formData.append('data', JSON.stringify(novaNoticia));
                Array.from(galeria).map((img, index) => {
                    var cod = index + 1;
                    formData.append('gallery' + cod, img);
                });
                await axios.post(URL_CADASTRAR_NOTICIA, formData);
                setExibirModal(true);
            }
        } catch (err) {
            setExibirModalErro(true);
        }
    }

    function visivelCapa() {
        return exibirImagens ? null : 'hidden';
    }

    function visivelImagens() {
        return exibirImagens ? 'mt-3' : 'hidden';
    }

    function visivel() {
        return props.visivel ? 'mt-3' : 'hidden';
    }

    function handleImage(event){
        setImagem(event.target.files[0]);
    }

    function handleGallery(event){
        setGaleria(event.target.files);
    }

    function handleTxtTitulo(event){
        setTitulo(event.target.value);
    }

    function handleTxtConteudo(event){
        setConteudo(event.target.value);
    }

    function handleFecharModal(){
        setExibirModal(false);
        props.handleExibirTabela();
        props.recarregarNoticias(true);
    }

    function handleFecharModalErro(){
        setExibirModalErro(false);
        props.handleExibirTabela();
        props.recarregarNoticias(true);
    }

    return ( 
        <div
            className={visivel()}>
            <Card >
                <Card.Header>
                    <h2>
                        Notícia
                    </h2>
                </Card.Header>
                <Card.Body>
                    <Container >
                        <Form 
                            onSubmit={salvarNoticia}>
                            <Form.Group
                                as={Row}>
                                <Form.Label
                                        column
                                        md={2}>
                                    Codigo:
                                </Form.Label>
                                <Form.Label
                                        column
                                        md={10}>
                                    {codigo}
                                </Form.Label>
                            </Form.Group>
                            <Form.Group
                                as={Row}
                                className="mt-2">
                                <Form.Label
                                        column
                                        md={2}>
                                    Capa (PNG ou JPG):
                                </Form.Label>
                                <Col 
                                    md={5}>
                                    <Form.Control
                                        type="file"
                                        onChange={handleImage}
                                        accept="image/png, image/jpeg"
                                        ref={capaElement} />
                                </Col>
                                <Form.Label
                                        column
                                        md={2}
                                        className={visivelCapa()}>
                                    Capa Atual:
                                </Form.Label>
                                <Col 
                                    md={3}
                                    className={visivelCapa()}>
                                    <Image 
                                        src={URL_IMG + capa}
                                        thumbnail />
                                </Col>
                            </Form.Group>
                            <Form.Group
                                as={Row}
                                className="mt-2">
                                <Form.Label
                                        column
                                        md={2}>
                                    Título:
                                </Form.Label>
                                <Col 
                                    md={10}>
                                    <Form.Control
                                        type="text"
                                        value={titulo} 
                                        onChange={handleTxtTitulo} />
                                </Col>
                            </Form.Group>
                            <Form.Group
                                as={Row}
                                className="mt-2">
                                <Form.Label
                                        column
                                        md={2}>
                                    Descrição:
                                </Form.Label>
                                <Col 
                                    md={10}>
                                    <Form.Control
                                        as="textarea" 
                                        rows={10}
                                        value={conteudo} 
                                        onChange={handleTxtConteudo} />
                                </Col>
                            </Form.Group>
                            <Form.Group
                                as={Row}
                                className="mt-2">
                                <Form.Label
                                        column
                                        md={2}>
                                    Galeria (PNG ou JPG):
                                </Form.Label>
                                <Col 
                                    md={10}>
                                    <Form.Control
                                        type="file" multiple
                                        onChange={handleGallery}
                                        accept="image/png, image/jpeg"
                                        ref={galeriaElement} />
                                </Col>
                            </Form.Group>
                            <Form.Group 
                                as={Row} 
                                className="mt-3">
                                <GaleriaImagens 
                                    visivel={exibirImagens}
                                    imagens={galeria}/>
                            </Form.Group>
                            <Form.Group 
                                as={Row} 
                                className="mt-3">
                                <Col 
                                    className="text-end"
                                    md={6}>
                                    <Button 
                                        variant="danger"
                                        onClick={props.handleExibirTabela} >
                                        Cancelar
                                    </Button>
                                </Col>
                                <Col 
                                    className="text-start"
                                    md={6}>
                                    <Button 
                                        variant="primary" 
                                        type="submit">
                                        Salvar
                                    </Button>
                                </Col>
                            </Form.Group>
                        </Form>
                    </Container>
                </Card.Body>
            </Card>
            <Modal 
                    show={exibirModal} 
                    onHide={handleFecharModal} >
                    <Modal.Header closeButton>
                        <Modal.Title>
                            Sucesso
                        </Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                        Notícia salva com sucesso!
                    </Modal.Body>
                    <Modal.Footer>
                        <Button 
                            variant="success"
                            onClick={handleFecharModal}>
                            Continuar
                        </Button>
                    </Modal.Footer>
                </Modal>
                <Modal 
                    show={exibirModalErro} 
                    onHide={handleFecharModalErro}>
                    <Modal.Header closeButton>
                        <Modal.Title>
                            Erro
                        </Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                        Erro ao adicionar tarefa, tente novamente em instantes.
                    </Modal.Body>
                    <Modal.Footer>
                        <Button 
                            variant="warning"
                            onClick={handleFecharModalErro}>
                            Fechar
                        </Button>
                    </Modal.Footer>
                </Modal>
        </div>
        
     );
}

