import { 
    faEdit, 
    faTrashAlt,
    faSortUp,
    faSortDown,
    faPlus
} from '@fortawesome/free-solid-svg-icons';
import { 
    Button
} from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import PropTypes from 'prop-types';

function Secao(props) {

    let key = 0;
    let nivel = 1;

    function gerarItemSecao(codigo){
        let secao = [];
        if(nivel === 1){
            secao = props.secoes.filter(secao => {
                return parseInt(secao.nivel) === nivel;
            });
        }
        else {
            secao = props.secoes.filter(secao => {
                return ((parseInt(secao.nivel) === nivel) && (parseInt(secao.cod_parente) === parseInt(codigo)));
            });
        }
        if(secao.length === 0){
            return null;
        }
        nivel++;
        let items = [];
        secao.map(item => {
            items.push(gerarItem(item));
            items.push(gerarSecao(item.codigo));
        });
        return(items);
    }

    function gerarSecao(codigo){
        return(
            <ul key={key++}>
                {gerarItemCadastro()} 
                {gerarItemSecao(codigo)}
            </ul>
        );
    }

    function gerarItem(item){
        return(
            <li key={key++}>{item.descricao}
            &nbsp;
                <FontAwesomeIcon className={'btn btn-outline-primary btn-sm'}
                    icon={faEdit} />
            &nbsp;
            <FontAwesomeIcon className={'btn btn-outline-danger btn-sm'}
                icon={faTrashAlt} />
            &nbsp;
            <FontAwesomeIcon className={'btn btn-outline-dark btn-sm'}
                icon={faSortUp} />
            &nbsp;
            <FontAwesomeIcon className={'btn btn-outline-dark btn-sm'}
                icon={faSortDown} />
            </li>
        );
    }

    function gerarItemCadastro(){
        if(nivel < 4){
            return (<li key={key++} className="mb-1">
                <FontAwesomeIcon className={'btn btn-outline-dark btn-sm'}
                    icon={faPlus} />
                &nbsp;
                Nova Secao 
                | 
                &nbsp;
                <FontAwesomeIcon className={'btn btn-outline-dark btn-sm'}
                    icon={faPlus} />
                &nbsp;
                Novo Documento</li>);
        }
        else{
            return (<li key={key++} className="mb-1">Novo Documento</li>);
        }
    }

    return (
        <ul key={key++}>
            {gerarItemCadastro()}    
            {gerarItemSecao("0")}
        </ul>
    );
}

Secao.propTypes = {
    secoes: PropTypes.array.isRequired
}

export default Secao;
