import { 
    faEdit, 
    faTrashAlt,
    faSortUp,
    faSortDown,
    faPlus
} from '@fortawesome/free-solid-svg-icons';
import { 
    Button
} from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import PropTypes from 'prop-types';

function Secao(props) {

    let key = 0;
    let nivel = 1;

    function gerarItemSecao(codigo){
        let listSecao = [];
        if(nivel === 1){
            listSecao = props.secoes.filter(secao => {
                return parseInt(secao.nivel) === nivel;
            });
        }
        else {
            listSecao = props.secoes.filter(secao => {
                return ((parseInt(secao.nivel) === nivel) && (parseInt(secao.cod_parente) === parseInt(codigo)));
            });
        }
        if(listSecao.length === 0){
            return null;
        }
        nivel++;
        let items = [];
        listSecao.map(secao => {
            items.push(gerarDescrSecao(secao));
            items.push(gerarSecao(secao));
        });
        return(items);
    }

    function gerarSecao(secao){
        return(
            <ul key={key++}>
                {gerarItemCadastro(secao)}
                {gerarDocumento(secao)}
                {gerarItemSecao(secao.codigo)}
            </ul>
        );
    }

    function gerarDocumento(secao){
        let items = [];
        props.documentos.map(documento => {
            if(parseInt(documento.secao) === parseInt(secao.codigo)){
                items.push(gerarDescrDocumento(secao, documento));
            }
        });
        return(items);
    }

    function gerarDescrSecao(secao) {
        return(
            <li key={key++} className="mb-1">
                <b>{secao.descricao}</b>
                &nbsp;
                <FontAwesomeIcon 
                    className={'btn btn-outline-primary btn-sm'}
                    icon={faEdit} />
                &nbsp;
                <FontAwesomeIcon 
                    className={'btn btn-outline-danger btn-sm'}
                    icon={faTrashAlt} />
                &nbsp;
                <FontAwesomeIcon 
                    className={'btn btn-outline-dark btn-sm'}
                    icon={faSortUp} />
                &nbsp;
                <FontAwesomeIcon className={'btn btn-outline-dark btn-sm'}
                    icon={faSortDown} />
            </li>
        );
    }

    function gerarDescrDocumento(secao, documento){
        return(
            <li key={key++} className="mb-1">
                {documento.descricao}
                &nbsp;
                <FontAwesomeIcon 
                    className={'btn btn-outline-primary btn-sm'}
                    icon={faEdit} />
                &nbsp;
                <FontAwesomeIcon 
                    className={'btn btn-outline-danger btn-sm'}
                    icon={faTrashAlt} />
                &nbsp;
                <FontAwesomeIcon 
                    className={'btn btn-outline-dark btn-sm'}
                    icon={faSortUp} />
                &nbsp;
                <FontAwesomeIcon className={'btn btn-outline-dark btn-sm'}
                    icon={faSortDown} />
            </li>
        );
    }

    function gerarItemCadastro(secao){
        console.log(secao);
        // if(parseInt(secao.nivel) < 4){
        //     return (<li key={key++} className="mb-1">
        //             <Button 
        //                 className={'btn btn-success btn-sm'}>
        //                 <FontAwesomeIcon 
        //                     icon={faPlus} />
        //                 &nbsp;
        //                 Nova Secao 
        //             </Button>
        //             &nbsp;
        //             <Button 
        //                 className={'btn btn-success btn-sm'}>
        //                 <FontAwesomeIcon 
        //                     icon={faPlus} />
        //                 &nbsp;
        //                 Nova Documento 
        //             </Button>
        //         </li>);
        // }
        // else{
            // return (<li key={key++} className="mb-1">
            //         <Button 
            //             className={'btn btn-success btn-sm'}>
            //             <FontAwesomeIcon 
            //                 icon={faPlus} />
            //             &nbsp;
            //             Nova Documento
            //         </Button>
            //     </li>);
        // }
    }

    return (
        <div>
            {gerarItemCadastro()}    
            {gerarItemSecao("0")}
        </div>
    );
}

Secao.propTypes = {
    secoes: PropTypes.array.isRequired,
    documentos: PropTypes.array.isRequired
}

export default Secao;
